mapper.select:
SELECT id,title,channel_id,category_id ,user_id, created,status,hot ,picture,complainCnt FROM cms_article WHERE deleted=0
AND status=#{value}
ORDER BY id DESC

SELECT id,title,channel_id,category_id ,user_id, created,picture
  FROM cms_article
  WHERE status = 1 AND deleted=0
  	AND channel_id=#{channelId}
  
  	AND category_id=#{catId}
     
  ORDER BY id DESC
